package com.hrms.util;

public class PaiChart {
	
	private String name;
	private String value;
	
	public PaiChart() {
		super();
		// TODO Auto-generated constructor stub
	}

	public PaiChart(String name, String value) {
		super();
		this.name = name;
		this.value = value;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getValue() {
		return value;
	}

	public void setValue(String value) {
		this.value = value;
	}
	
	

}
